mapper.update:

UPDATE application_status
SET first_pass = 'Y', interview_date = #{interview_date}
WHERE status_no = #{status_no}


UPDATE application_status
SET first_pass = 'F'
WHERE status_no = #{status_no}


UPDATE application_status
SET final_pass = 'Y'
WHERE status_no = #{status_no}


UPDATE application_status
SET final_pass = 'F'
WHERE status_no = #{status_no}
